mapper.select:
SELECT DiagnosisDiseaseTypeName, COUNT(*) AS count FROM DailyHealthStatus
WHERE CheckInDate = #{now}
GROUP BY DiagnosisDiseaseTypeName

SELECT UserID AS userId, CheckInDate AS checkInDate, CheckInTime AS checkInTime, IsHealth AS isHealth, DiseaseTypeName AS diseaseTypeName, DiagnosisDiseaseTypeName AS diagnosisDiseaseTypeName FROM DailyHealthStatus WHERE 1 = 1
AND UserID IN
#{id}
ORDER BY CheckInDate DESC, CheckInTime DESC
LIMIT #{infoNumber}
